            elem = elems.eq(0);

        var outerWidth = parseInt(elem.data('outerWidth') || elem.outerWidth(true)),
            width = parseInt(elem.data('width') || elem.css('width')),
            qty = elems.length;

        elem.data({'outerWidth':outerWidth, 'width':width});

        var extraWidth = outerWidth - width;
