base : function(arg){
	var _this = $(this),
		_arg = $.extend({
			node : 'li',
			click_node : '.jBtnArea a'
		},arg || {}),
		_ids = [],
		_base_url = INTERFACE.actJshop.ms;

	function _init(){
		_get_ids();
		_get_state();
	}

	function _get_ids(){
		_this.find(_arg.node).each(function(index,n){
			_ids.push($(n).attr('prompt-id'));
		});				
	}

	function _get_state(){
		$.ajax({
			url : _base_url,
			type : 'POST',
			data : {promoId : _ids.join(',')},
			dataType : 'jsonp',
			success : function(data){
				if(data.result){
					data.values.each(function(index,n){
						if(n.status == '0'){
							$(_this).find('li[prompt-id="' + n.id + '"]').attr('class',_arg.overCls).find(_arg.click_node).attr('href','#none');
						}
						else if(n.status == '1'){
							$(_this).find('li[prompt-id="' + n.id + '"]').attr('class',_arg.underwayCls);
						}
						else{
							$(_this).find('li[prompt-id="' + n.id + '"]').attr('class',_arg.waitCls).find(_arg.click_node).attr('href','#none');;
						}
					});
				}
			}
		});
	}
	_init();
}

jingdou : function(arg){
	var _this = $(this),
		_args = $.extend({
			pprice:'.Jpprice',
			jingNum:'.Jjnum'
		},arg || {}),
		_ids = [];

	function _init(){
		_get_skuids();
		if(!_ids.length) return;
		_get_jindou_info();
	}

	function _get_skuids(){
		_this.find('[prompt-id]').each(function(index,n){
			_ids.push($(n).attr('skuid') + '-' + $(n).attr('prompt-id'));
		});
	}

	function _get_jindou_info(){
		$.ajax({
			url : '//jprice.360buy.com/skuprice/' + _ids.join(',') + '-1-1.html',
			dataType : 'jsonp',
			cache : false,
			success : function(data){
				if(data&&data.length){
					data.each(function(index,n){
						if(n.s != 1) return;
						var __node = _this.find('[skuid=' + n.sid + ']');
						__node.find(_args.pprice).html(n.pp);
						__node.find(_args.jingNum).html(n.jbn || 0);
					});
				}
			}
		})
	}
	_init();
}

addFavorite: function(args) {
	if(args == undefined){
		if(validateData($(this).attr('module-param'))){
			var args = eval('('+$(this).attr('module-param')+')');
		}
	}

	var _this = this,
		param = $.extend({title:'京东商城', url:'//www.jd.com'},args),
		title = param.title,
		url = param.url;

	$(_this).css('cursor','pointer');
	$(_this).click(function(e){
		if (document.all) {
	        window.external.AddFavorite(url, title);
	    } else if (window.sidebar) {
	        window.sidebar.addPanel(title, url, "");
	    } else {
	        alert("对不起，您的浏览器不支持此操作!\n请您使用菜单栏或Ctrl+D收藏本站。");
	    }
	});		    
}

autoCenter : function(args){
	var _this = $(this),
		param = $.extend({autoMiddleNode:'.userDefinedArea', isOwnLink : true} , args || {}),
		node = _this.find(param.autoMiddleNode),
		isOwnLink = param.isOwnLink;

	alignCenter();
	$(window).resize(function(){
		alignCenter();
	});

	function alignCenter(){
		var extra = node.width()-_this.width();
		if(extra>0){
			node.css({'margin-left':-extra/2});
		}else{
			node.css('margin','0 auto');
		}
	}

	//判断自定义编辑器里面的图片是否有链接  20150907 by cdwanchuan@jd.ccom
	if(isOwnLink && location.href.indexOf('visualEditing')!= -1){
		var img = node.find('img');
		img.each(function(i,e){
			if((jQuery(e).parent()[0].tagName!= 'A') && !jQuery(e).attr('usemap')){
				var relativePar = jQuery(jQuery(e)[0].offsetParent);
				if(relativePar.length && jQuery(e).length){
					var top = jQuery(e).offset().top - relativePar.offset().top + jQuery(e).height()/2 -11,
						left = jQuery(e).width()/2 - 31;
					node.append('<img src="//img11.360buyimg.com/cms/jfs/t2353/241/73326232/691/b8435b2b/55ed2da1N0a73abac.png" width="62" height="23" style="position:absolute; left:' + left +'px; top:' + top + 'px;" />');
				}
			}
		});
	}

sideSlip:function(args){

   	//此方法应用的图片不使用懒加载
	   var imgs = $(this).find('img');
	   imgs.each(function(index,n){
		  $(n).attr('src',$(n).attr('original'));
		  $(n).removeAttr('original');
		  $(n).removeClass('J_imgLazyload');
	   });

		if(args == undefined){
			if(validateData($(this).attr('module-param'))){
				var args = eval('('+$(this).attr('module-param')+')');
			}
		}

		var _this = this,
			param = $.extend({vertical:'top', verticalValue:170, horizontal:'left', horizontalValue:0, zindex:10},args),
			vertical = param.vertical,
			verticalValue = param.verticalValue,
			horizontal = param.horizontal,
			horizontalValue = param.horizontalValue,
			zindex = param.zindex;

		//判断参数个数
		var count = 0;
		for(var i in param){
			count++;
		}
		//如果垂直是top，水平是right
		if(count == 5 && vertical == 'top' && verticalValue >= 170 && horizontal == 'right'){
			setInterval(function(){
				verticalValue += (getTop() + param.verticalValue - verticalValue)/20;
				$(_this).css({'position':'absolute', 'top':verticalValue, 'right':horizontalValue, 'zIndex':zindex});
			},20);
		}
		//如果垂直是top，水平是left
		if(count == 5 && vertical == 'top' && verticalValue >= 170 && horizontal == 'left'){
			setInterval(function(){
				verticalValue += (getTop() + param.verticalValue - verticalValue)/20;
				$(_this).css({'position':'absolute', 'top':verticalValue, 'left':horizontalValue, 'zIndex':zindex});
			},20);
		}
		//如果垂直是bottom，水平是right
		if(count == 5 && vertical == 'bottom' && horizontal == 'right'){
			var a = $(window).height()-$(_this).height()-param.verticalValue,
				b = a;
			setInterval(function(){
				a += (getTop() + b - a)/20;
				$(_this).css({'position':'absolute', 'top':a, 'right':horizontalValue, 'zIndex':zindex});
			},20);
		}
		//如果垂直是bottom，水平是left
		if(count == 5 && vertical == 'bottom' && horizontal == 'left'){
			var a = $(window).height()-$(_this).height()-param.verticalValue,
				b = a;
			setInterval(function(){
				a += (getTop() + b - a)/20;
				$(_this).css({'position':'absolute', 'top':a, 'left':horizontalValue, 'zIndex':zindex});
			},20);
		}
		//如果垂直是bottom，水平是middle
		if(count == 5 && vertical == 'bottom' && horizontal == 'middle'){
			var a = $(window).height()-$(_this).height()-param.verticalValue,
				b = a;

			setInterval(function(){
				a += (getTop() + b - a)/20;
				$(_this).css({'position':'absolute','top':a, 'left':horizontalValue, 'margin-left':-($(_this).width()/2), 'zIndex':zindex});
			},20);
		}
		function getTop(){
			return (document.documentElement.scrollTop||document.body.scrollTop||0)-(document.documentElement.clientTop||document.body.clientTop||0);
		}
	}

addEllipsis:function(args){

       if(!args)return;
       var _this = this,
               param = jQuery.extend({title:'li', count:20, text:'...'}, args),
               elem = jQuery(_this).find(param.title),
               reg = /\s|\,|\.|\!|\'|\"|\;|\:|\t|\n|\r/g;
//               reg = new RegExp("\\s|\\,|\\.|\\!|\\'|\\\"|\\;|\\:|\\t|\\n|\\r", "g");
       elem.each(function(index, ele){
           var _textNode = ele.firstChild,
                   _textValue = _textNode.nodeValue;
           if(_textNode && _textNode.nodeType == 3 && _textNode.length >= param.count){
               var _tempValue = _textValue.substring(0, param.count - param.text.length);
               var _char = _textValue.charAt(param.count - param.text.length);
               if(reg.test(_char)){
                   _textNode.nodeValue = _tempValue + param.text;
               }else{
                   var _arr = _tempValue.match(reg);
                   _tempValue = _tempValue.substring(0, _tempValue.lastIndexOf(_arr[_arr.length - 1]) + 1) + param.text;
                   _textNode.nodeValue = _tempValue;
               }
           }
       });

   }

addToCart : function(args){
        var param = $.extend({
            node : 'li',
            img : '.jPic img',//飞入节点
            cart : '.jBtnArea a',//点击节点
            property : 'data-sku',//获取节点li上的skuid
            jdCart : '#settleup-2013',//购物车
            cartNum : '#shopping-amount',//购物车数量
            timer : 500
        }, args || {}),
            that = $(this),
            node = that.find(param.node),
            jdCart = $(param.jdCart),
            cartNum = jdCart.find(param.cartNum);

        node.each(function(i, e){
            var img = $(e).find(param.img),
                cart = $(e).find(param.cart);

            cart.click(function(){
                smarket.add(cart, $(e).attr(param.property), img, jdCart, cartNum, param.timer);
            });
        });

        var smarket = {
            ele : null,
            sku : null,
            ptype : null,
            pcount : null,
            add : function(a,b,img, jdCart, cartNum, timer, c,d){
                0 != b &&(this.ele = a, this.sku = b, this.ptype = d || 1, this.pcount = c || 1, this.img = img, this.jdCart = jdCart, this.cartNum = cartNum, this.timer = timer, this.versonAnimate());
            },

            versonAnimate : function(){
                var a = this;
                clearTimeout(a.timer);
                a.timer = null;
                this.addToCart();
                var b = a.img,
                    c = b.height(),
                    d = b.width(),
                    e = b.offset().left,
                    f = b.offset().top,
                    g = $(document).scrollTop(),
                    h = a.jdCart.offset().left + 50,
                    i = a.jdCart.offset().top,
                    j = $('<div class="flyGoods jGoodsRecommend20140909" style="position:absolute;z-index: 10;"></div>'),
                    l = 25;

            var t = $(a.ele);
                j.html(b.clone()).css({
                    left: e + d / 2 - l + Math.round(40Math.random() + 0 - 20),
                    top: f + c/2 - 1 + Math.round(40Math.random() + 0 - 20)
                });

                j.appendTo('body');
                j.animate({
                    top : t.offset().top,
                    left : t.offset().left + t.width() - 50
                    },a.timer,'easeOutCirc',function(){
                        j.animate({
                            left : h,
                            top : i,
                            opacity : 0.1
                        },a.timer,'easeInOutQuint',function(){
                            a.getCartNum();
                            j.remove();
                        });
                    });
            },

            addToCart : function(){
                var a = INTERFACE.reBuyForOrderCenter + "?wids={PID}&nums={PCOUNT}";
                a = a.replace("{PID}", this.sku).replace("{PCOUNT}", this.pcount).replace("{PTYPE}", this.ptype),
                jQuery.ajax({
                    url: a,
                    dataType: "jsonp"
                });
            },
            getCartNum : function(){
                var that = this;
                jQuery.ajax({
                    url : INTERFACE.miniCartServiceNew,
                    data : {
                        method : 'GetCart'
                    },
                    dataType : 'jsonp',
                    success : function(data){
                        try{
                            that.cartNum.html(data.Cart.Num);
                        }
                        catch(e){
                        }
                    }
                });
            }
        }
    }

attentStoreOrAct: function(args) {
        var param = jQuery.extend({
                attentNode: 'a',  //点击元素
                shopId: 'shop-id', //店铺ID（绑定在节点伪属性，可为空）
                actId: 'act-id',  //活动ID（绑定在节点伪属性，可为空）
                typeUrl: 'url'    //活动或店铺链接（绑定在节点伪属性）
            }, args || {}),
            followUrl = '',
			data = {},
            _this = $(this),
            _node = _this.find(param.attentNode),
            appType = 0; //链接类型
        _node.each(function(){
            var $this = $(this),
                $parent = $this.parent();
            // 调用公共方法获取链接类型（0表示采销活动，1表示店铺活动，2表示店铺）
            appType =returnUrlType($this.attr(param.typeUrl));
            $this.attr('data-type', 2); //表示默默关注
            $parent.addClass('attentScope');
            switch (appType) {
                case 0:
                case 1:
                    // 活动
                    $parent.saleAttent({
                        attentType: 'activity',
                        node: param.attentNode,
                        dataId: 'act-id',
                        current: 'favor-handle',
                        activityType: appType
                    });
                    break;
                case 2:
                    // 店铺
                    $parent.saleAttent({
                        attentType: 'vender',
                        node: param.attentNode,
                        dataId: 'shop-id',
                        current: 'favor-handle'
                    });
                    break;
                default:
                    break;
            }
        });
    }

如{autoFillNode:'li', xInner:0, yInner:0, minWidth:' ', xOuter:' ', yOuter:' '}
1、autoFillNode为需要计算宽度的节点，默认为li；xInner为节点之间的横向间距；yInner为节点之间的纵向间距;
2、minWidth为节点除内外边距、边框之后的宽度，如果没传则自动获取。如果最终算出来的宽度小于最小宽度，则不做改变;
3、xOuter为节点父级左右边距，默认为空，表示两边没有间距；如果传入值大于0，则两边增加传入的间距值；如果等于0，则表示两边完全不要间距，包括每一行第一个节点和最后一个节点的边距。yOuter和xOuter相反;
4、length 每一行的数量有三种方式：1是自定义传；2是根据每一行的宽度和单个的宽度计算能放下的数量；3是当一行的数量不够占一行的数量

autoFill : function(args){
        var param = $.extend({autoFillNode:'li', xInner:0, yInner:0, minWidth:' ', xOuter:' ', yOuter:' ', isEqual:false, length:''} , args||{}),
            _this = $(this),
            node = _this.find(param.autoFillNode),
            xInner = parseInt(param.xInner),
            yInner = parseInt(param.yInner),
            minWidth = parseInt(param.minWidth)?parseInt(param.minWidth):node.width(),
            xOuter = param.xOuter,
            yOuter = param.yOuter,
            isEqual = param.isEqual,
            length;


        //有最外层横向两边距
        if(xOuter !== 0){
            //是否需要平均节点宽度
            if(isEqual){
                length = node.length;
            }else if(param.length > 0){
                length = param.length;
            }else{
                length = parseInt((_this.width() - xOuter*2)/minWidth);
            }

            var width = (_this.width() - (length+1)*xInner - xOuter*2 - length*(parseFloat(node.css('padding-left')) + parseFloat(node.css('padding-right')) + parseFloat(node.css('border-left-width')) + parseFloat(node.css('border-right-width'))))/length;

            if(width < minWidth)return;};

            init();
            _this.css('padding-left',xOuter);
        }

        //不要最外层横向两边距
        if(xOuter === 0){
            //是否需要平均节点宽度
            if(isEqual){
                length = node.length;
            }else if(param.length > 0){
                length = param.length;
            }else{
                length = parseInt(_this.width()/minWidth);
            }

            var width = (_this.width() - (length-1)*xInner - length*(parseFloat(node.css('padding-left')) + parseFloat(node.css('padding-right')) + parseFloat(node.css('border-left-width')) + parseFloat(node.css('border-right-width'))))/length;

            if(width < minWidth)return;};

            init();
            getRowFirst();
        }

        //默认状态，最外层横向两边距不存在
        if(xOuter ===' '){
            //是否需要平均节点宽度
            if(isEqual){
                length = node.length;
            }else if(param.length > 0){
                length = param.length;
            }else{
                length = parseInt(_this.width()/minWidth);
            }

            var width = (_this.width() - (length+1)*xInner - length*(parseFloat(node.css('padding-left')) + parseFloat(node.css('padding-right')) + parseFloat(node.css('border-left-width')) + parseFloat(node.css('border-right-width'))))/length;

            if(width < minWidth)return;};

            init();
        }

        //有最外层纵向两边距
        if(yOuter != 0){
            _this.css('padding-bottom',yInner+yOuter);
            _this.css('padding-top',yOuter);
        }

        //不要最外层纵向两边距
        if(yOuter === 0){
            getRow();
        }

        //默认状态，最外层纵向两边距不存在
        if(yOuter === ' '){
            _this.css('padding-bottom',yInner);
        }


        node.css('width', width);

        //初始化节点
        function init(){
            _this.css('overflow','hidden');

            if(node.css('margin-left')||node.css('margin-right')){
                node.css({'margin-left':0, 'margin-right':0});
            }

            node.css('margin-top',yInner);
            node.css('margin-left',xInner);
        }

        //获取每一行的第一个节点
        function getRowFirst(){
            node.each(function(i,e){
                if(i % length === 0){
                    $(e).css('margin-left',0);
                }
            });
        }

        //获取任意多行节点
        function getRow(){
            var number = [1];
            node.each(function(i,e){
                for(var j = 0; j < number.length; j++){
                    if(i >= length(number[j] - 1) && i < lengthnumber[j]){
                        $(e).css('margin-top',0);
                    }
                }
            });
        }

    }

