					_show_message(_tips);
					return false;
				}
				if(_val == $('#newTag').attr('placeholder'))
					return false;
				$('<li isNewAdd="true"><a class="current" href="javascript:;" isCheck="true">' + _val + '</a></li>').click(function(){
					_chooseTag(this);
				}).insertBefore( $('#att-tag-new'));
                if($('#newTags li[isNewAdd]').length >= 3){
					$('#att-tag-new').hide();
				}
                $('#newTag').val('');
			}

			function _chooseTag(obj){
				var isCheck=$(obj).find('a').attr("isCheck");
				if( 'undefined' == typeof isCheck || isCheck=='false' ){
					$(obj).find('a').attr("isCheck","true");
					$(obj).find('a').addClass("current");
				}else{
					$(obj).find('a').attr("isCheck","false");
					$(obj).find('a').removeClass("current");
				}
			}

			function _show_message(msg){
				$('#follow_error_msg').html(msg);
		    	 $('#follow_error_msg').show();
		    	 if(_msg_timer){
		    		 clearTimeout(_msg_timer);
		    	 }
	    		 _msg_timer = setTimeout(function(){
		    		  $('#follow_error_msg').hide();
		     	 }, 3000);
			}

			function _followed(){
				if(_user_cnt){
					_user_cnt.find('.p1,.p2,.p3').hide();
					_user_cnt.find('.p3').show();
					_open_box();
				}
				else{
					_get_follow_num(function(){
						_show_box('followedDiv','已关注过该店铺');
					});
				}
			}

			function _get_follow_num(handle){
				var __url = INTERFACE.venderFollow.queryForCount,
					data = {sysName : 'mall.jd.com'};
				$.ajax({
					url : __url,
					data : data,
					dataType : 'jsonp',
					async : false,
					success : function(data){
						_followVM.find('#followSuccessDiv #followedNum,#followedDiv #followedNum').html('您已关注' + data.data + '个店铺，');
						handle();

					}
				});
			}

			function _show_box(cnt,title){
                $('.thickbox').html('');
				$.jdThickBox({
					width : 300,
					height : 80,
					title : title || '关注失败',
					source : _followVM.find('#' + cnt).html()
				});
			} 

			function _center(obj){
				var __w = obj.outerWidth(),__h = obj.outerHeight();
				obj.css({
					position:'absolute',
					left : ($(window).width() - __w)/2 + $(window).scrollLeft() + 'px',
					top : ($(window).height() - __h)/2 + $(window).scrollTop() + 'px',
					zIndex : 10000005
				});
			}

			function _mask(){
				if(!$('.thickdiv').length){
					$('<div class="thickdiv"></div>').appendTo('body');
				}
			}
			_init();
			_event_init();	
		}
